mapper.insert:
		INSERT INTO MEMBERS (
   		   EMAIL
 		 , ID
 		 , PASSWORD
 		 , SALT )
		VALUES (
   		   #{email}
 		 , #{id}
 		 , #{password}
 		 , #{salt} )

mapper.select:
		SELECT COUNT(1)
  		  FROM MEMBERS
 		 WHERE EMAIL = #{_parameter}

		SELECT COUNT(1)
  		  FROM MEMBERS
 		 WHERE ID = #{_parameter}

		SELECT SALT
  		  FROM MEMBERS
 		 WHERE EMAIL = #{_parameter}
